// Bootstrap Health Check
async function bootstrapHealthCheck(): Promise<boolean> {
  const checks = [
    { name: 'Neo4j', check: () => neo4jService.healthCheck() },
    { name: 'SQLite', check: () => getDatabase().prepare('SELECT 1').get() }
  ];

  for (const { name, check } of checks) {
    try {
      await check();
      console.log(`‚úÖ ${name} healthy`);
